import * as React from 'react';
import {Container, Button, Modal, InputGroup, FormControl} from 'react-bootstrap'
import {useState, useEffect} from 'react';
import Note from './Note.js'
import API from './API.js'


export default function Notes()
{
    const [show, SetShow] = useState(false);
    const [notesList, SetNotesList] = useState([]);
    const [login, SetLogin] =useState(1);
       
       

    


    async function GetAllNotes()
    {
        var response = await API.get("/notes");
        SetNotesList(response.data);
    }

    useEffect(() =>
    {
      GetAllNotes()  

    }, [0])
  
    async function SaveNote()
    {
        var note = 
        {
            date: document.getElementById("inputDate").value,
            title: document.getElementById("inputTitle").value,
            description: document.getElementById("inputDescription").value,
        }
        
        await API.post("/notes", note);
        GetAllNotes();
        
        SetShow(false);
    }
    function OrderNewest()
    {
        var aux= 0;
        var notesListCopy=[...notesList];
        
        for (let i =0; i<notesListCopy.length; i++){
            for(let j= i+1;j<notesListCopy.length -1;i++){
                if (Date.parse(notesListCopy[i].date)< Date.parse(notesListCopy[j].date)){
                    aux = notesListCopy[i];
                    notesListCopy[i]=notesListCopy[j];
                    notesListCopy[j]=aux;
                }

            }

        }
        SetNotesList(notesListCopy);

    }

    function OrderOldest(){
        
        var aux= 0;
        var notesListCopy=[...notesList];
        
        for (let i =0; i <notesListCopy.length; i++){
            for(let j= i+1;j <notesListCopy.length -1;i++){
                if (Date.parse(notesListCopy[i].date) > Date.parse(notesListCopy[j].date)){
                    aux = notesListCopy[i];
                    notesListCopy[i]=notesListCopy[j];
                    notesListCopy[j]=aux;
                }

            }

        }
        SetNotesList(notesListCopy);


    }

    
    if(login==1){
        return(<>
        <Container className = "notes">
            <h1>
                tela de login
            </h1>
            <p>
                mamaco
            </p>
            <InputGroup>
                    <InputGroup.Text> Titulo </InputGroup.Text>             
                    <FormControl type="password" id = "inputPassword"></FormControl>
                </InputGroup>
                <br/>
                <br/>
            <Button variant = "success" onClick ={() => {}}> confirmar </Button>
           
        </Container>
        </>);

    }
    else{

    

    return(<>
        <Container className = "notes">
            <h1>
                Minhas anotações
            </h1>
            <p>
                Aqui será usado para adicionar anotações
            </p>
            <Button variant = "success" onClick ={() => {SetShow(true)}}> Criar uma nova anotação</Button>
            <Button variant="info" onClick={()=>{OrderNewest()}}>mais recentes</Button>
            <Button variant="secondary" onClick={()=>{OrderOldest()}}>mais antigos</Button>
        </Container>

        <Container fluid>
            {notesList.map(item => <Note title = {item.title} date = {item.date} description = {item.description} id ={item.id}  GetAllNotes={GetAllNotes}/>)}
        </Container>    

        <Modal show = {show}>
            <Modal.Header>
                <Modal.Title> Nova Anotação</Modal.Title>
            </Modal.Header>

            <Modal.Body>
                <InputGroup>
                  <InputGroup.Text> Date </InputGroup.Text>
                  <input type ="date" id = "inputDate"></input>
                </InputGroup>

                <br/>
                
                <InputGroup>
                    <InputGroup.Text> Titulo </InputGroup.Text>             
                    <FormControl id = "inputTitle"></FormControl>
                </InputGroup>

                <br/>

                <InputGroup>
                    <InputGroup.Text>Descrição </InputGroup.Text>
                <FormControl id = "inputDescription" as = "textarea"></FormControl>
                </InputGroup>

            </Modal.Body>
                <Button variant = "secondary" onClick = {() => {SetShow(false)}}>Cancelar</Button>
                <Button variant = "primary" onClick = {() => {SaveNote();}}>Adicionar </Button>
            <Modal.Footer>

            </Modal.Footer>
        </Modal>
    </>);
}
}
